import { useEffect, useState } from 'react';
import CustomDropdown from '../components/CustomDropdown';
import { avalibleNumber } from '../types/avalibleNumberType';
import { numberService } from '../services/numberService';
import { User } from '../types/userType';
import { userService } from '../services/userService';
import { useNavigate } from 'react-router-dom';

function CreateUser() {

    const [avalibleNumber, setAvalibleNumbers] = useState<avalibleNumber[]>([])
    const [user, setUser] = useState<User>({
        name: "",
        surname: "",
        org_id: 0 });
    const empresas = ['Iberdrola', 'Endesa', 'Gas Natural'];
    const navigate = useNavigate();


    useEffect(() => {
        const fetchNumbers = async () => {
            try {
                
                const response = await numberService.getAvalibleNumber();
                setAvalibleNumbers(response)
            } catch (error) {
                
            }
        }
        fetchNumbers();
    }, []);


    const handleClick = () => {
        userService.createUser(user)
        navigate(`/home/${user.org_id}`);
    }


    return(
        <>
            <div style={{ width: '39%', margin: '20px auto', border: '1px solid #ccc', padding: '20px', borderRadius: '10px', backgroundColor: '#f2f2f2' }}>
                <h4 style={{ marginBottom: "16px" }}>Create a new user</h4>
                <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '10px' }}>
                    <input type="text" className="form-control" placeholder="Name name" aria-label="Username" style={{ marginRight: '10px', borderRadius: '5px' }} onChange={(e) => setUser((newUser) => ({...newUser,name: e.target.value}))} />
                    <input type="text" className="form-control" placeholder="Surname" aria-label="Surname" style={{ marginLeft: '10px', borderRadius: '5px' }} onChange={(e) => setUser((newUser) => ({...newUser,surname: e.target.value}))}/>
                </div>
                <div style={{ display: 'flex', justifyContent: 'center', gap: "30%" }}>
                <div style={{ width: '100%' }}>
                    <CustomDropdown numbers={avalibleNumber} selectedValue={undefined} user={user} setUser={setUser} />
                </div>
                <div style={{ width: '100%' }}>
                    <CustomDropdown orgs={empresas} selectedValue={undefined} user={user}  setUser={setUser}/>
                </div>
                </div>
                <button type="button" className="btn btn-success" disabled={user.org_id != 0 && user.name != "" && user.surname != "" ? false : true} onClick={handleClick}><i className="fa-solid fa-plus"></i> Add new user</button>
            </div>
        </>
    )
}

export default CreateUser;
